def dataFrameToFasta(df: DataFrame, name: String = "transcript_stable_id", sequence: String = "sequence") =
    df.select(name, sequence).as[(String, String)].rdd.aggregate("")(
      { case (acc, (n, seq)) => acc + ">" +n + "\n" + seq.sliding(80, 80).reduce(_ + "\n" + _) + "\n" },
      { case (acc1, acc2) => acc1 + acc2 }
    )

def writeFastaTo(df: DataFrame, fileName: String, fieldName: String = "transcript_stable_id", folder: String = "/pipelines/indexes/genage/lags") = {
    import java.io.PrintWriter
    import java.io.File
    val sequences_fasta = dataFrameToFasta(df, fieldName)
    val path = folder + "/" + fileName
    new PrintWriter(path) { write(sequences_fasta); close }
    path
}

def simplify = udf[String, String]{ str => (str.indexOf("."), str.indexOf(".")) match {
    case (-1, -1) => str
    case (-1, j) => str.substring(0, j)
    case (i, -1) => str.substring(0, i)
    case (i, j) => str.substring(0, j).substring(0, i)
}}

def prepare_transcripts(sufix: String, species: String): DataFrame = {
    val needs_simplification = Set("mouse", "human")
    val long = longevity(sufix)(species)
    val conv = conversions(species).select("transcript_stable_id", "xref").distinct
    val trans = {if(needs_simplification.contains(species)) transcripts(species).dataset.withColumn("transcript_stable_id", simplify($"contigName")) else transcripts(species).dataset.withColumnRenamed("contigName", "transcript_stable_id")}.select("transcript_stable_id", "description", "sequence")
    val trans_entrez = trans.join(conv, Seq("transcript_stable_id"))
    val trans_filtered = long.select("Entrez Gene ID", "Ensembl ID").distinct.join(trans_entrez, $"Entrez Gene ID" ===$"xref")
    //val trans_alt = long.select("Ensembl ID").distinct.join(trans, $"Ensembl ID" === $"transcript_stable_id")
    //(trans_alt.count, trans_filtered.count, trans_alt.join(trans_filtered, Seq("transcript_stable_id")).count)
    trans_filtered
}
def write_transcripts(sufix: String, species: String, path: String = "/pipelines/indexes/genage/lags"): (String, DataFrame) = {
    val trans = prepare_transcripts(sufix, species)
    val name = s"${sufix}_${species}_sequences.fasta"
    writeFastaTo(trans, name, folder = path) -> trans
}
